import React, { Component } from 'react'
import axios from 'axios';

export default class App extends Component {
constructor(props){
    super(props);

    this.state={
        posts:[]
    };

}

componentDidMount(){
    this.retrievePost();
}

retrievePost(){
    axios.get("http://localhost:8000/posts").then(res =>{
        if(res.data.success){
            this.setState({
                posts:res.data.existingPosts
            });

            console.log(this.state.posts)
        }
    })

}

  render() {
    return (
      <div>
        <p>All Posts</p>
